DROP TABLE IF EXISTS `wp_4_gf_addon_feed`;

CREATE TABLE `wp_4_gf_addon_feed` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `form_id` mediumint(8) unsigned NOT NULL,
