/*Table structure for table `wp_4_posts` */

DROP TABLE IF EXISTS `wp_4_posts`;

CREATE TABLE `wp_4_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`(191)),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=MyISAM AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

/*Data for the table `wp_4_posts` */

insert  into `wp_4_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (1,1,'2016-10-11 05:51:33','2016-10-11 05:51:33','Welcome to <a href=\"http://localhost/akyodo/\">AKW Network</a>. This is your first post. Edit or delete it, then start blogging!','Hello world!','','publish','open','open','','hello-world','','','2016-10-11 05:51:33','2016-10-11 05:51:33','',0,'http://localhost/akyodo/author/?p=1',0,'post','',1),(2,1,'2016-10-11 05:51:33','2016-10-11 05:51:33','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost/akyodo/author/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','publish','closed','open','','sample-page','','','2016-10-11 05:51:33','2016-10-11 05:51:33','',0,'http://localhost/akyodo/author/?page_id=2',0,'page','',0),(3,8,'2016-09-14 10:53:49','2016-09-14 17:53:49','LINK: <a href=\"http://www.rockblossom.org/\" target=\"_blank\">Rock Blossom Sangha: Brooklyn Community of Mindfulness</a>','&quot;love and justice are not two: diversity & inclusion in sanghas&quot; - Rock Blossom Sangha','&nbsp;','publish','open','open','','love-and-justice-are-not-two-diversity-inclusion-in-sanghas-rock-blossom-sangha','','','2016-09-14 10:53:49','2016-09-14 17:53:49','',0,'http://localhost/akyodo/author/2016/09/14/love-and-justice-are-not-two-diversity-inclusion-in-sanghas-rock-blossom-sangha/',0,'post','',0),(4,1,'2016-10-19 20:49:43','2016-10-19 20:49:43','','10400485_20129780988_8566_n','','inherit','open','closed','','10400485_20129780988_8566_n','','','2016-10-19 20:49:43','2016-10-19 20:49:43','',3,'http://localhost/akyodo/author/wp-content/uploads/sites/4/2016/10/10400485_20129780988_8566_n.jpg',0,'attachment','image/jpeg',0),(5,1,'2016-10-19 20:49:43','2016-10-19 20:49:43','','10400485_20129740988_739_n','','inherit','open','closed','','10400485_20129740988_739_n','','','2016-10-19 20:49:43','2016-10-19 20:49:43','',3,'http://localhost/akyodo/author/wp-content/uploads/sites/4/2016/10/10400485_20129740988_739_n.jpg',0,'attachment','image/jpeg',0),(6,1,'2016-10-25 03:38:24','0000-00-00 00:00:00','','Auto Draft','','auto-draft','open','open','','','','','2016-10-25 03:38:24','0000-00-00 00:00:00','',0,'http://localhost/akyodo/author/?p=6',0,'post','',0),(7,8,'2016-09-14 09:27:10','2016-09-14 16:27:10','https://www.youtube.com/watch?v=vqHQuVnPGEk\r\n\r\nReverend angel Kyodo williams speaks with campaign and citizen activists from the Stand community and beyond on how activists can use Radical Dharma to confront white supremacy and privilege. The racial violence of the weeks preceding the seminar drove home the urgency of the topic for the 170 participants.\r\n\r\nRev. angel presented on Radical Dharma and Activism: Confronting Inequity in Our Own Work for Social Change, exploring how we can use mindfulness to confront white supremacy and white privilege in our lives and our work. Stand executive director Todd Paglia introduced Rev. angel. After Rev. angel’s opening remarks, Todd asked Rev. angel an initial set of questions and then brought in questions from the audience.\r\n\r\nThe webinar included discussions on why using the phrase white supremacy is critical, why it’s important to see differences between people even while acknowledging that we are all one, why a mindfulness approach helps people confront inequity, and how each person needs to learn to love and free their own self on the way to promoting love and freedom for everyone else.\r\n\r\nLINK: <a href=\"http://www.stand.earth/\" target=\"_blank\">Stand</a>','Radical Dharma and Activism: Confronting Inequity in Our Own Work for Social Change - sponsored by Stand','','publish','open','open','','radical-dharma-and-activism-confronting-inequity-in-our-own-work-for-social-change-sponsored-by-stand','','','2016-09-14 09:27:10','2016-09-14 16:27:10','',0,'http://localhost/akyodo/author/2016/09/14/radical-dharma-and-activism-confronting-inequity-in-our-own-work-for-social-change-sponsored-by-stand/',0,'post','',0),(9,1,'2016-10-25 06:00:30','2016-10-25 06:00:30','','our-duty-to-win_sm-bw','','inherit','open','closed','','our-duty-to-win_sm-bw','','','2016-10-25 06:00:30','2016-10-25 06:00:30','',7,'http://localhost/akyodo/author/wp-content/uploads/sites/4/2016/10/our-duty-to-win_sm-bw.jpg',0,'attachment','image/jpeg',0),(10,1,'2016-10-25 06:00:30','2016-10-25 06:00:30','','Radical Dharma and Activism: Confronting Inequity in Our Own Work for Social Change &#8211; sponsored by Stand','','inherit','open','closed','','radical-dharma-and-activism-confronting-inequity-in-our-own-work-for-social-change-sponsored-by-stand-2','','','2016-10-25 06:00:30','2016-10-25 06:00:30','',7,'http://localhost/akyodo/author/wp-content/uploads/sites/4/2016/10/radical-dharma-and-activism-confronting-inequity-in-our-own-work-for-social-change-8211-sponsored-by-stand.jpg',0,'attachment','image/jpeg',0),(11,8,'2016-08-05 05:09:28','2016-08-05 12:09:28','As part of the 2016 Mindfulness Retreat for People of Color,  Rev. angel Kyodo williams offers a presentation and reading based on the newly published book, Radical Dharma.\r\n\r\nIn this, Rev. angel invites us into a long overdue conversation about race alongside love and liberation. As part of this conversation, Rev. angel speaks about the social construct of whiteness, while calling us back into our humanity and to be in touch with the \"thing underneath the thing.\"\r\n\r\n<strong>A few excerpts: </strong>\r\n<blockquote>\"...we have the greatest potential to actually pull it up at its roots. Interestingly enough, we have that opportunity through this gift of the Dharma that actually invites us to be able to look with clear seeing eyes at things that are not real...that are figments of our imagination and figments of other people\'s imaginations...Race is a grand social figment.\"\r\n\r\n\"As Dharma teachers, the way we survived the aggression and violence of a white dominant form of Dharma that was coming to us---is that we got underneath the thing. We got to the thing underneath the thing. We had to get down to who we really were. And simultaneously appreciate our teachers, our lineages, the texts as they were given but also have a fundamental trust in who we were in our basic humanity.\"\r\n\r\n\"What does Dharma become when it\'s expressed through the experience of someone who lives in a Black body.\" --Rev. angel Kyodo williams</blockquote>','Deer Park Dharmacast','\"What does Dharma become when it\'s expressed through the experience of someone who lives in a Black body.\" --Rev. angel Kyodo williams','publish','open','open','','deer-park-dharmacast','','','2016-08-05 05:09:28','2016-08-05 12:09:28','',0,'http://localhost/akyodo/author/2016/08/05/deer-park-dharmacast/',0,'post','',0),(12,1,'2016-10-25 06:01:17','2016-10-25 06:01:17','','buddhaboxes','','inherit','open','closed','','buddhaboxes','','','2016-10-25 06:01:17','2016-10-25 06:01:17','',11,'http://localhost/akyodo/author/wp-content/uploads/sites/4/2016/10/buddhaboxes.jpg',0,'attachment','image/jpeg',0),(13,1,'2016-10-25 06:01:17','2016-10-25 06:01:17','','deerpark_dharmacast','','inherit','open','closed','','deerpark_dharmacast','','','2016-10-25 06:01:17','2016-10-25 06:01:17','',11,'http://localhost/akyodo/author/wp-content/uploads/sites/4/2016/10/deerpark_dharmacast.png',0,'attachment','image/png',0);

