insert  into `wp_5_postmeta`(`meta_id`,`post_id`,`meta_key`,`meta_value`) values (1,2,'_wp_page_template','default'),(5,4,'_wp_attached_file','2016/10/10400485_20129780988_8566_n.jpg'),(6,4,'_wp_attachment_metadata','a:5:{s:5:\"width\";i:604;s:6:\"height\";i:453;s:4:\"file\";s:39:\"2016/10/10400485_20129780988_8566_n.jpg\";s:5:\"sizes\";a:6:{s:9:\"thumbnail\";a:4:{s:4:\"file\";s:39:\"10400485_20129780988_8566_n-150x150.jpg\";s:5:\"width\";i:150;s:6:\"height\";i:150;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:6:\"medium\";a:4:{s:4:\"file\";s:39:\"10400485_20129780988_8566_n-300x225.jpg\";s:5:\"width\";i:300;s:6:\"height\";i:225;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:9:\"portfolio\";a:4:{s:4:\"file\";s:39:\"10400485_20129780988_8566_n-540x340.jpg\";s:5:\"width\";i:540;s:6:\"height\";i:340;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:16:\"author-pro-image\";a:4:{s:4:\"file\";s:39:\"10400485_20129780988_8566_n-360x453.jpg\";s:5:\"width\";i:360;s:6:\"height\";i:453;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:6:\"slider\";a:4:{s:4:\"file\";s:39:\"10400485_20129780988_8566_n-604x400.jpg\";s:5:\"width\";i:604;s:6:\"height\";i:400;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:14:\"rpwe-thumbnail\";a:4:{s:4:\"file\";s:37:\"10400485_20129780988_8566_n-45x45.jpg\";s:5:\"width\";i:45;s:6:\"height\";i:45;s:9:\"mime-type\";s:10:\"image/jpeg\";}}s:10:\"image_meta\";a:12:{s:8:\"aperture\";s:1:\"0\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:0:\"\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";s:1:\"0\";s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:1:\"0\";s:3:\"iso\";s:1:\"0\";s:13:\"shutter_speed\";s:1:\"0\";s:5:\"title\";s:0:\"\";s:11:\"orientation\";s:1:\"0\";s:8:\"keywords\";a:0:{}}}'),(7,5,'_wp_attached_file','2016/10/10400485_20129740988_739_n.jpg'),(8,5,'_wp_attachment_metadata','a:5:{s:5:\"width\";i:453;s:6:\"height\";i:604;s:4:\"file\";s:38:\"2016/10/10400485_20129740988_739_n.jpg\";s:5:\"sizes\";a:6:{s:9:\"thumbnail\";a:4:{s:4:\"file\";s:38:\"10400485_20129740988_739_n-150x150.jpg\";s:5:\"width\";i:150;s:6:\"height\";i:150;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:6:\"medium\";a:4:{s:4:\"file\";s:38:\"10400485_20129740988_739_n-225x300.jpg\";s:5:\"width\";i:225;s:6:\"height\";i:300;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:9:\"portfolio\";a:4:{s:4:\"file\";s:38:\"10400485_20129740988_739_n-453x340.jpg\";s:5:\"width\";i:453;s:6:\"height\";i:340;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:16:\"author-pro-image\";a:4:{s:4:\"file\";s:38:\"10400485_20129740988_739_n-360x570.jpg\";s:5:\"width\";i:360;s:6:\"height\";i:570;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:6:\"slider\";a:4:{s:4:\"file\";s:38:\"10400485_20129740988_739_n-453x400.jpg\";s:5:\"width\";i:453;s:6:\"height\";i:400;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:14:\"rpwe-thumbnail\";a:4:{s:4:\"file\";s:36:\"10400485_20129740988_739_n-45x45.jpg\";s:5:\"width\";i:45;s:6:\"height\";i:45;s:9:\"mime-type\";s:10:\"image/jpeg\";}}s:10:\"image_meta\";a:12:{s:8:\"aperture\";s:1:\"0\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:0:\"\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";s:1:\"0\";s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:1:\"0\";s:3:\"iso\";s:1:\"0\";s:13:\"shutter_speed\";s:1:\"0\";s:5:\"title\";s:0:\"\";s:11:\"orientation\";s:1:\"0\";s:8:\"keywords\";a:0:{}}}'),(27,6,'_is_video',''),(26,6,'audio_link','http://home.adelphi.edu/~BOHN/talks/Angel_Kyodo.mp3'),(25,6,'_edit_lock','1476910165:1'),(24,6,'_wp_convertkit_post_meta',''),(23,6,'_wp_convertkit_post_meta',''),(21,6,'_edit_last','1'),(22,6,'_edit_last','1'),(36,7,'_edit_last','1'),(29,6,'_thumbnail_id','5'),(35,7,'_edit_last','1'),(33,8,'_wp_attached_file','2016/10/buddhaboxes.jpg'),(34,8,'_wp_attachment_metadata','a:5:{s:5:\"width\";i:500;s:6:\"height\";i:333;s:4:\"file\";s:23:\"2016/10/buddhaboxes.jpg\";s:5:\"sizes\";a:4:{s:9:\"thumbnail\";a:4:{s:4:\"file\";s:23:\"buddhaboxes-150x150.jpg\";s:5:\"width\";i:150;s:6:\"height\";i:150;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:6:\"medium\";a:4:{s:4:\"file\";s:23:\"buddhaboxes-300x200.jpg\";s:5:\"width\";i:300;s:6:\"height\";i:200;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:16:\"author-pro-image\";a:4:{s:4:\"file\";s:23:\"buddhaboxes-360x333.jpg\";s:5:\"width\";i:360;s:6:\"height\";i:333;s:9:\"mime-type\";s:10:\"image/jpeg\";}s:14:\"rpwe-thumbnail\";a:4:{s:4:\"file\";s:21:\"buddhaboxes-45x45.jpg\";s:5:\"width\";i:45;s:6:\"height\";i:45;s:9:\"mime-type\";s:10:\"image/jpeg\";}}s:10:\"image_meta\";a:12:{s:8:\"aperture\";s:1:\"0\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:0:\"\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";s:1:\"0\";s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:1:\"0\";s:3:\"iso\";s:1:\"0\";s:13:\"shutter_speed\";s:1:\"0\";s:5:\"title\";s:0:\"\";s:11:\"orientation\";s:1:\"1\";s:8:\"keywords\";a:0:{}}}'),(37,7,'_wp_convertkit_post_meta',''),(38,7,'_wp_convertkit_post_meta',''),(39,7,'_edit_lock','1477375199:1'),(40,7,'audio_link','http://s3.amazonaws.com/BZCaudio/2014-02-08-Dharma-AngelKyodoWilliams-BuddhaMeetsBuddha.mp3'),(41,7,'_is_video',''),(43,7,'_thumbnail_id','8'),(48,9,'_edit_last','1'),(47,9,'_edit_last','1'),(49,9,'_oembed_e299dffc31285e08884dcc094e6a8da9','<iframe width=\"500\" height=\"400\" scrolling=\"no\" frameborder=\"no\" src=\"https://w.soundcloud.com/player/?visual=true&url=http%3A%2F%2Fapi.soundcloud.com%2Ftracks%2F264847471&show_artwork=true&maxwidth=500&maxheight=750\"></iframe>'),(50,9,'_oembed_e299dffc31285e08884dcc094e6a8da9','<iframe width=\"500\" height=\"400\" scrolling=\"no\" frameborder=\"no\" src=\"https://w.soundcloud.com/player/?visual=true&url=http%3A%2F%2Fapi.soundcloud.com%2Ftracks%2F264847471&show_artwork=true&maxwidth=500&maxheight=750\"></iframe>'),(51,9,'_oembed_time_e299dffc31285e08884dcc094e6a8da9','1474344618'),(52,9,'_oembed_time_e299dffc31285e08884dcc094e6a8da9','1474344618'),(53,9,'_oembed_a313117e29107c023d8cba57c81c8870','{{unknown}}'),(54,9,'_oembed_a313117e29107c023d8cba57c81c8870','{{unknown}}'),(55,9,'_wp_old_slug','on-liberation-justice-and-love-good-life-project'),(56,