/*Table structure for table `wp_5_posts` */

DROP TABLE IF EXISTS `wp_5_posts`;

CREATE TABLE `wp_5_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`(191)),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

/*Data for the table `wp_5_posts` */

insert  into `wp_5_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (1,1,'2016-10-11 05:53:00','2016-10-11 05:53:00','Welcome to <a href=\"http://localhost/akyodo/\">AKW Network</a>. This is your first post. Edit or delete it, then start blogging!','Hello world!','','publish','open','open','','hello-world','','','2016-10-11 05:53:00','2016-10-11 05:53:00','',0,'http://localhost/akyodo/speaker/?p=1',0,'post','',1),(2,1,'2016-10-11 05:53:00','2016-10-11 05:53:00','This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost/akyodo/speaker/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!','Sample Page','','publish','closed','open','','sample-page','','','2016-10-11 05:53:00','2016-10-11 05:53:00','',0,'http://localhost/akyodo/speaker/?page_id=2',0,'page','',0),(6,8,'2016-09-14 10:53:49','2016-09-14 17:53:49','LINK: <a href=\"http://www.rockblossom.org/\" target=\"_blank\">Rock Blossom Sangha: Brooklyn Community of Mindfulness</a>','&quot;love and justice are not two: diversity & inclusion in sanghas&quot; - Rock Blossom Sangha','&nbsp;','publish','open','open','','love-and-justice-are-not-two-diversity-inclusion-in-sanghas-rock-blossom-sangha','','','2016-09-14 10:53:49','2016-09-14 17:53:49','',0,'http://localhost/akyodo/speaker/2016/09/14/love-and-justice-are-not-two-diversity-inclusion-in-sanghas-rock-blossom-sangha/',0,'post','',0),(4,1,'2016-10-19 20:43:42','2016-10-19 20:43:42','','10400485_20129780988_8566_n','','inherit','open','closed','','10400485_20129780988_8566_n','','','2016-10-19 20:43:42','2016-10-19 20:43:42','',0,'http://localhost/akyodo/speaker/wp-content/uploads/sites/5/2016/10/10400485_20129780988_8566_n.jpg',0,'attachment','image/jpeg',0),(5,1,'2016-10-19 20:43:43','2016-10-19 20:43:43','','10400485_20129740988_739_n','','inherit','open','closed','','10400485_20129740988_739_n','','','2016-10-19 20:43:43','2016-10-19 20:43:43','',0,'http://localhost/akyodo/speaker/wp-content/uploads/sites/5/2016/10/10400485_20129740988_739_n.jpg',0,'attachment','image/jpeg',0),(7,8,'2016-09-14 10:51:02','2016-09-14 17:51:02','LINK: <a href=\"https://brooklynzen.org/\" target=\"_blank\">Brooklyn Zen Center</a>','&quot;buddha meets buddha: love and justice are not two&quot; - Brooklyn Zen Center','&nbsp;','publish','open','open','','buddha-meets-buddha-love-and-justice-are-not-two-brooklyn-zen-center','','','2016-09-14 10:51:02','2016-09-14 17:51:02','',0,'http://localhost/akyodo/speaker/2016/09/14/buddha-meets-buddha-love-and-justice-are-not-two-brooklyn-zen-center/',0,'post','',0),(8,1,'2016-10-25 06:00:00','2016-10-25 06:00:00','','buddhaboxes','','inherit','open','closed','','buddhaboxes','','','2016-10-25 06:00:00','2016-10-25 06:00:00','',0,'http://localhost/akyodo/speaker/wp-content/uploads/sites/5/2016/10/buddhaboxes.jpg',0,'attachment','image/jpeg',0),(9,8,'2016-09-14 10:43:42','2016-09-14 17:43:42','<p style=\"text-align: justify;\"></p>\r\n<p style=\"text-align: justify;\">Reverend angel Kyodo williams was a guest on the Good Life Project podcast.</p>\r\n<p style=\"text-align: justify;\">In This Episode, You’ll Learn:</p>\r\n\r\n<ul style=\"text-align: justify;\">\r\n 	<li>How growing up in New York City profoundly shaped Rev. angel’s life.</li>\r\n 	<li>How moments to awaken are all around us, if we just open to them.</li>\r\n 	<li>How to move closer to a liberated life.</li>\r\n 	<li>How race and the consciousness movement interact with each other.</li>\r\n 	<li>The #1 component contributing to a full, complete life.</li>\r\n 	<li>The difference between grasping and aspiration.</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">Mentioned in This Episode:</p>\r\n\r\n<ul style=\"text-align: justify;\">\r\n 	<li>Connect with Rev. Angel: a<a href=\"http://angelkyodowilliams.com/\" target=\"_blank\">ngel Kyodo williams</a> | <a href=\"http://angelkyodowilliams.com/category/blog/\" target=\"_blank\">Blog</a> | <a href=\"https://www.facebook.com/zenchangeangel\" target=\"_blank\">Facebook</a> | <a href=\"https://twitter.com/zenchangeangel\" target=\"_blank\">Twitter</a> | <a href=\"https://27daysofchange.com/coming-2-2/\" target=\"_blank\">27 Days of Change</a></li>\r\n 	<li><a href=\"http://www.amazon.com/Being-Black-Living-Fearlessness-Compass/dp/0140196307\" target=\"_blank\">Being Black: Zen and the Art of Living with Fearlessness and Grace</a> by angel Kyodo Williams</li>\r\n 	<li><a href=\"http://radicaldharma.org/\" target=\"_blank\">Radical Dharma</a></li>\r\n 	<li><a href=\"http://www.richardwiseman.com/Luck.shtml\" target=\"_blank\">Richard Wiseman</a></li>\r\n 	<li><a href=\"https://www.amazon.com/Zen-Mind-Beginners-Shunryu-Suzuki-ebook/dp/B00I8USOM0?ie=UTF8&amp;btkr=1&amp;ref_=dp-kindle-redirect\" target=\"_blank\">Zen Mind, Beginner’s Mind</a> by Shunryu Suzuki</li>\r\n</ul>\r\n<p style=\"text-align: justify;\">Be sure to <a href=\"http://www.goodlifeproject.com/\" target=\"_blank\">subscribe</a> to our weekly Good Life Updates and <a href=\"http://www.goodlifeproject.com/itunes\" target=\"_blank\">listen on iTunes to make sure you never miss an episode!</a></p>\r\n<p style=\"text-align: justify;\">—</p>\r\n<p style=\"text-align: justify;\">Photo credit: Christine Alicino</p>\r\n<strong>Listen to the interview here:</strong> <a href=\"http://www.goodlifeproject.com/rev-angel-kyodo-williams/?t=radio\" target=\"_blank\">http://www.goodlifeproject.com/rev-angel-kyodo-williams/?t=radio</a>','On Liberation, Justice and Love - Good Life Project','In This Episode, You’ll Learn: How growing up in New York City profoundly shaped Rev. angel’s life. How moments to awaken are all around us, if we just open to them.','publish','open','open','','on-liberation-justice-love-good-life-project','','','2016-09-14 10:43:42','2016-09-14 17:43:42','',0,'http://localhost/akyodo/speaker/2016/09/14/on-liberation-justice-love-good-life-project/',0,'post','',0),(10,8,'2016-09-10 12:52:48','2016-09-10 19:52:48','<p style=\"text-align: center\">An interview with angel Kyodo williams</p>\r\n<p style=\"text-align: center\">By Garrison Institute</p>\r\n<p style=\"text-align: left\">In an upcoming<a href=\"http://ebiz.jccmanhattan.org/PersonifyEBusiness/Default.aspx?TabID=416&amp;pid=647004968&amp;_ga=1.12229876.1199380218.1456349628\" target=\"_blank\"> “Garrison Talks at the JCC” event in New York City on August 10, “Bridging Spirituality and Activism,”</a> Zen teacher angel Kyodo williams and meditation teacher Sharon Salzberg will discuss challenging questions about the relationship between personal and social transformation. How do racism and privilege prevent our collective awakening? How can each one of us affect the shortcomings not only of our own minds but also of our communities?</p>\r\nLeading up to this event, we spoke with williams about how she approaches these questions and others in her new book, <a href=\"https://www.amazon.com/Radical-Dharma-Talking-Race-Liberation/dp/1623170982\" target=\"_blank\">Radical Dharma: Talking Race, Love, and Liberation</a>.\r\n\r\n<strong>The central message of Radical Dharma is that personal and social transformation must be brought together, with an extra emphasis on those who have been historically marginalized. Do I have that right?</strong>\r\n\r\nThat’s certainly an aspect of it, but what I mean first and foremost is that the way that spiritual truth has been held in this country has been partial. People have chosen to look at particular aspects of spiritual truth. The statement that I’m trying to make in Radical Dharma is that we actually have to have a complete lens on the way in which we show up for everything and that includes how we show up in our social presentation.\r\n\r\n<strong>Can you say more about the particular aspects of spiritual truth that have been emphasized? What’s missing?</strong>\r\n\r\nWhat’s been emphasized are personal and, to some extent, interpersonal spheres of behavior and understanding. If something is inside the known sphere of my comfort zone—or relative comfort zone—then I can look at it. If it fits inside of all the ways that I have chosen to organize my worldview, then I can apply the spiritual teachings. But if it doesn’t fit inside of my worldview, then I won’t apply the teachings.\r\n\r\nRadical Dharma‘s message is about applying spiritual teachings and truth to the entire sphere of what makes up our lives. An example of the sort of thing I’m talking about is when people talk about “right livelihood,” but they don’t look at capitalism. They think, “Within capitalism, I’m going to apply these teachings.” This approach takes capitalism as a given. This is a neat cordoning off of my known sphere of awareness. I might add that this works in multiple directions. Inside social justice activist circles, someone might apply their sense of truth towards social liberation, but not necessarily to their own self-care.\r\n\r\n<strong>Many of us find ourselves participating in systems that lead to suffering—we’ve been talking about capitalism—and want to do something about it but don’t know where to start. How do you do something about capitalism?</strong>\r\n\r\nThere is wisdom in asking, “How do I respond given my particular situation and location and reality?” That question arises out of the choice to see things clearly. How one person should approach answering that question would be different for another person—for example, it’s going to look differently for people in different places on the the income scale. Another example is that I’m not ready to tell someone that is an Alaska Native to be vegan. On the other hand, most of the people that have free access to protein-rich food choices should probably step away from so much meat consumption.\r\n\r\nThere is wisdom that arises out of the willingness to look at your situation clearly. You can look at your situation in a radical way—look at the systems that compose your reality—as opposed to picking and choosing what you pay attention to.\r\n\r\n<strong>Can you say more about the importance of bringing personal and social transformation together? Do you approach them in different ways?</strong>\r\n\r\nIn some ways they are the same and in some ways they are different. For the aspects of a social problem that touch you personally, teachings on personal liberation are helpful. There’s a way to avoid becoming so overwhelmed by an emotion so that you’re unable to actually see a thing clearly. But that does not mean that all will be resolved by navigating only our personal anger when there is also a reason to have collective anger—and that collective anger is powerful resource of energy that can be directed. For example, the Black Lives Matter movement had to maintain a certain kind of stance that we would call angry from a social perspective in order to sustain enough energy to be taken seriously. The organizers didn’t allow the anger to consume them personally and individually, but the movement could not suddenly run off to Martin Luther King forms of non-violence and peace and happiness, because we’re not there yet.\r\n\r\nThere’s a kind of arc in our personal anger that can allow us to transmute it into a powerful response on the collective level. And in many ways, in order to get to the collective levels, we actually have to do the work internally so that it is not consuming us personally. In fact, it becomes sort of like faking the anger. You inhabit a space, but you’re not attached to that space as representing who you are, who we are, or whoever the “they” collective is. You can say that the expression of collective anger is actually skillful given certain conditions.\r\n\r\n<strong>It sounds like you have to be able to hold multiple spaces at once.</strong>\r\n\r\nYes. An interesting thing about racialization in this country is that groups of people that are marginalized are much more used to inhabiting multiple spaces. They’re much more accustomed to belonging to both a collective as well as being an individual. And, frankly, they’re much more often viewed in terms of their collective identity.\r\n\r\n<strong>So are you suggesting that the teachings need to be adapted in different ways because people inhabit different spaces?</strong>\r\n\r\nIt alters everything, right? This is why we need diversity in teaching. If you’re not accustomed to inhabiting space in a collective identity, you don’t know how to teach about inhabiting space in a collective identity. You don’t know how to teach about navigating the emotions of a collective identity as opposed to the personal identity. It’s not about shaming people who teach in a certain way. It’s just to say that one of the reasons we need diversity is because you wouldn’t even know how to speak to certain particularities. In the same way, I wouldn’t know how to speak to the particularity of being a person that’s of the original nations of this land. I can point at it, but I don’t have the grieving sense of the loss of a connection to these lands that my people once owned, and so on. I have other experiences and can teach accordingly.','Inhabiting Multiple Spaces','','publish','open','open','','inhabiting-multiple-spaces','','','2016-09-10 12:52:48','2016-09-10 19:52:48','',0,'http://localhost/akyodo/speaker/2016/09/10/inhabiting-multiple-spaces/',0,'post','',0),(11,8,'2016-07-08 00:27:24','2016-07-08 07:27:24','[caption id=\"attachment_10459\" align=\"aligncenter\" width=\"294\"]<a href=\"http://angelkyodowilliams.com/wp-content/uploads/2016/09/ajdavis_20140920_47591.jpg\"><img class=\"wp-image-10459 \" src=\"http://angelkyodowilliams.com/wp-content/uploads/2016/09/ajdavis_20140920_47591-300x200.jpg\" alt=\"ajdavis_20140920_47591\" width=\"294\" height=\"196\" /></a> Zen teacher angel Kyodo williams. Photo by A. Jesse Jiryu Davis.[/caption]\r\n<h3 style=\"text-align: justify\">To make diversity real, says Zen teacher angel Kyodo williams, Buddhists must look deep into their own hearts.</h3>\r\n<blockquote>\r\n<p style=\"text-align: justify\"><em>Brooklyn Zen Center executive director Greg Snyder has spoken of a mind-meld that’s gone on between you and he, and BZC guiding teacher Teah Strozer. Let’s talk about that connection.</em></p>\r\n</blockquote>\r\n<p style=\"text-align: justify\">I’m a New Yorker. I lived in Fort Greene and had a little sitting group, an offshoot of my main practice home of Village Zendo. Coincidentally, I called it Brooklyn Zen Center because I believed that the practice could arise in the community, from the community. Most places of practice that I saw didn’t feel like that. They had a feeling of separateness. I knew that in order for Buddhist practice to take root amongst peoples not defined by a particular Western, white, middle-aged, middle-to-upper-middle-class construct, the practice had to go and meet people and not just expect them to come and find it.</p>\r\n<p style=\"text-align: justify\">I eventually went to California, and coming back and finding that this Brooklyn Zen Center was really doing the work of welcoming was an incredible relief to me, especially because they were doing it in such a compassionate, humble, present, and transparent way.</p>\r\n<p style=\"text-align: justify\"><em>How does that way manifest?</em></p>\r\n<p style=\"text-align: justify\">Many centers say, “Oh, we want to include people so let’s invite them to come.” But because we, as Western-worldview, dominant-paradigm folks have not done our work, we actually don’t know how to be welcoming. An invitation is a gesture. Welcoming, though, is open-armed hospitality. It’s in the heart. I think that what Brooklyn Zen has that many places don’t is in its leadership: people who continue to truly examine their own hearts and are willing to address the barriers and the hindrances to being welcoming.</p>\r\n<p style=\"text-align: justify\"><em>How can we make fundamental changes in ourselves so that we can be welcoming, not just inviting?</em></p>\r\n<p style=\"text-align: justify\">Meditation is awesome, but it’s not fast enough. People need to get trained. Training amps up the intensity, bringing things intentionally into our view so that we can work on what’s there to be worked on.</p>\r\n<p style=\"text-align: justify\"><em>What will that training help address?</em></p>\r\n<p style=\"text-align: justify\">The paradigms and the worldviews of white Western privilege, which have been developed for no other reason but to bestow privilege on a very small group of people and to create separation. That’s what the construct of whiteness was for right from the beginning.</p>\r\n<p style=\"text-align: justify\">This is not about bashing anybody for who they are or for their skin color. This is about a construct that was developed purely and solely in order to create separation and now that construct needs to be deconstructed. If we don’t do that, that separation will live on and it will do so, shamefully, in the midst of teachings that are so profoundly gifted with a language, with an approach, with a methodology, to unseat separation.</p>\r\n<p style=\"text-align: justify\"><em>It’s been some fifteen years since you presented these issues in your acclaimed book, Being Black. Are you writing?</em></p>\r\n<p style=\"text-align: justify\">I am. [You can read an exclusive excerpt of william’s book, Radical Dharma: Talking Race, Love and Liberation, written with Lama Rod Owens and Jasmine Syedullah, inside the September 2016 Lion’s Roar magazine. —Ed.] One thing I’m working on, that speaks directly to all this, is a book about my own shocked recognition that I have spent many years—in the vernacular of black folks back to the days of slavery—keeping my head down.</p>\r\n<p style=\"text-align: justify\"><em>How so?</em></p>\r\n<p style=\"text-align: justify\">By trying to stay smaller than was organic to the situation and to my experience, because I didn’t want to draw the ire of folks in the dharma world. And even though we don’t have any singular institution of Zen or Buddhism, there’s a power structure, one that is often unspoken. We can recognize it if we look at the Buddhist media and see who’s there—who are we being told to look at and listen to, and who is wielding those structures and who seeks to influence them. Many people I’ve spoken to say they don’t feel free, particularly folks of color and, in some traditions, women—each tradition has its own stuck place.</p>\r\n<p style=\"text-align: justify\">The apparent hierarchy that exists in some of our spiritual structures is there to support us but it’s being leveraged as a mask for power structures and privilege. In some places of practice it’s very male or heterosexual; in some places it’s other things. But pretty much all over the place, it’s pretty white.</p>\r\n<p style=\"text-align: justify\"><em>So is what you’re writing part memoir, part guidance for feeling empowered enough to no longer be quiet, or, as you put it, “small”?</em></p>\r\n<p style=\"text-align: justify\">Part memoir, part guidance, and a lot of critique. I’m also working on a collection of observations on our society, through what people would call a Buddhist lens. I would just say a lens of liberation. I look at what gets in the way, including the parasitic capitalism that we have not just as an economic structure but also as a religious structure. Capitalism has become a religion—many religions actually deify it at this point—and because of that, we’re letting it run amok with no real thought about how to change it.</p>\r\n<p style=\"text-align: justify\"><em>Does something like the People’s Climate March, which BZC was quite involved with, suggest that things are starting to turn around?</em></p>\r\n<p style=\"text-align: justify\">Hopefully the climate march is a disruption of a system that is consuming itself and all of us with it. The march did come from “the people”—it originated out of environmental justice organizations, not out of typical green groups and NGOs. It came out of brown and black and economically disadvantaged white communities, and indigenous peoples. It’s about across-the-board disruption. It’s about understanding that climate change is a leverage point for us to look at all of the failures, limitations, and places of separation and breakdown in our society.</p>\r\n<p style=\"text-align: justify\"><em>What does “disruption” mean? Does it mean voting with our dollars? Does it mean that if we don’t like what’s going on in the media, we become the media? What kinds of forms will disruption take?</em></p>\r\n<p style=\"text-align: justify\">Everything. First and foremost we need to take back our power, and our power exists in our participation in all of these structures. It will be a challenge because we were weaned on this system. We are bred to consume. And, so, for us to break our own chains is just as difficult as it was for blacks to resist the slavery that they’d been born into for generations and was all they’d ever known. The idea was that that system would continue forever because, once you had bred someone into the system, they didn’t know anything else.</p>\r\n<p style=\"text-align: justify\"><em>Are you hopeful?</em></p>\r\n<p style=\"text-align: justify\">Not in the sense of tomorrow, but I’m hopeful that the seed has been planted, that the irrelevance of the systems that continue to privilege small groups of people is laid bare now. We’re in this wonderful moment of going, “Oh, this doesn’t work. There are no winners in this.”</p>\r\n<p style=\"text-align: justify\"><em>How are we going to convince the captains of industry that they’re not winners?</em></p>\r\n<p style=\"text-align: justify\">I don’t think we need to. It’s like, “How are we going to convince the plantation owners to let go of their slaves?” We didn’t. We had to snatch the slaves from their arms.</p>\r\n<p style=\"text-align: justify\">There’s no such thing as being neutral. You’re either playing along with how things are or you are disrupting them and turning them on their head. Those are the only options.</p>\r\n<p style=\"text-align: justify\"><em>Pursuing the possibility of a new monastery, as BZC is doing, seems like a major step forward.</em></p>\r\n<p style=\"text-align: justify\">I don’t think I’ve ever been excited about a monastery before. [Laughter].</p>\r\n<p style=\"text-align: justify\"><em>Why you are excited about this monastery?</em></p>\r\n<p style=\"text-align: justify\">Because I can see myself there, whether that’s my physical body or not. I think that Greg and Teah and the leadership will plant the seeds for a deep practice home. The Western Buddhist world has spent too much time saying, “Oh, if we want people of color to participate, we can’t expect them to do real, deep practice.” With that attitude, people of color will never be fully accepted. As soon as Being Black came out, I was completely chagrined that I had invited people of color, through the book, into a place that was not welcoming. The good news is that BZC rings true. And the monastery, if it happens, will also be a place that is welcoming, and that’s a start.</p>\r\n<p style=\"text-align: justify\"><strong>Read the interview here:</strong> <a href=\"http://www.lionsroar.com/beyond-privilege-qa-angel-kyodo-williams/\">http://www.lionsroar.com/beyond-privilege-qa-angel-kyodo-williams/</a></p>','Beyond Privilege: a Q&A with angel Kyodo williams','','publish','open','open','','beyond-privilege-a-qa-with-angel-kyodo-williams','','','2016-07-08 00:27:24','2016-07-08 07:27:24','',0,'http://localhost/akyodo/speaker/2016/07/08/beyond-privilege-a-qa-with-angel-kyodo-williams/',0,'post','',0),(12,8,'2016-02-15 00:57:39','2016-02-15 08:57:39','<h3 style=\"text-align: justify\"><img class=\"alignleft\" src=\"https://www.eomega.org/sites/default/files/styles/article_md/public/images/articles/20130422-img_9220.jpg?itok=vdXLM_ox\" alt=\"American Buddhism &amp; Diversity\" width=\"318\" height=\"212\" />Being Black author <a href=\"http://www.eomega.org/workshops/teachers/angel-kyodo-williams-0\" target=\"_blank\">angel Kyodo williams</a> speaks about the evolution of diversity in American Buddhism and her work to promote inclusivity in Buddhism across communities</h3>\r\n<p style=\"text-align: justify\">When Being Black came out in 2000, I was chagrined by what I had done. I realized that, out of my love and appreciation for the dharma, I issued this invitation to people of color into a Buddhist American space that was not welcoming. Obviously there were pockets and exceptions—but for the most part, the Western Buddhist community did not want or know how to receive them. I had invited black folks to crash the private upper middle class party that was going on and it was just not wanted. Really, I was quite bereft for a while, feeling foolish and naïve.</p>\r\n<p style=\"text-align: justify\">If you asked me five years ago what has changed, I would have said not much. But I think that in the last little while, some interesting things have begun to signal the beginning of a change. People of color and people of color monks are really pushing in.</p>\r\n<p style=\"text-align: justify\">I’m shocked that I was writing about this 15 years ago and that I am now one of the people bringing conversations directly into still overwhelmingly white Buddhist communities, that it’s happening at all. I know that, because people in Buddhist communities, from different traditions, are inviting me to speak to them. People are genuine, receptive, open, vulnerable and they are coming out in force.</p>\r\n<p style=\"text-align: justify\">In general, something is happening now with the Black Lives Matter movement and the increasing inability to avoid how out of alignment this society is and how out of sync with its own teachings Buddhist communities are.</p>\r\n<p style=\"text-align: justify\">The most powerful thing, the most ordinary and extraordinary thing, is person to person. People are having actual relationships with actual people who are marginalized in all sorts of ways that aren’t acceptable. Real touch between one another in which people say “I can’t abide by that.” I have a partner that’s black—I can’t abide by that. My brother married a Latino woman and my niece and nephew are biracial—I can’t abide by that. My kid is queer—I can’t abide by that. They wouldn’t say it 15 years ago, but now that’s the new reality.</p>\r\n<p style=\"text-align: justify\"><strong>Read the interview here:</strong> https://www.eomega.org/article/15-years-later-american-buddhism-diversity</p>','15 Years Later: American Buddhism & Diversity','','publish','open','open','','15-years-later-american-buddhism-diversity','','','2016-02-15 00:57:39','2016-02-15 08:57:39','',0,'http://localhost/akyodo/speaker/2016/02/15/15-years-later-american-buddhism-diversity/',0,'post','',0);

